mapper.update:

update blog
set title = #{title,jdbcType=VARCHAR},
  keyword = #{keyword,jdbcType=VARCHAR},
  content = #{content,jdbcType=LONGVARCHAR}
where blog_id = #{blogId,jdbcType=BIGINT}

mapper.select:

select blog_id, user_id, create_time, update_time, title, keyword, readcount, content
from blog
where blog_id = #{blogId,jdbcType=BIGINT}

mapper.update:

update blog
set readcount = readcount+1
where blog_id = #{blogId,jdbcType=BIGINT}

mapper.select:

select blog_id, user_id, create_time, update_time, title, keyword, readcount, content
from blog
order by readcount desc limit #{blogNum}
